import React from 'react'
import Comment from './Comment'

const Post = ({post, index}) => {
 
    return (
        <div>
           <h1> Post :{index+1} </h1>
          <h2>User :{post.User} {post.text}</h2>
          {
               post.comments.map((cmnt,idx) => <Comment singleComment={cmnt} indx={idx}/> ) 
              
          }
         
          
        </div>
    )
}

export default Post
