import React from 'react'
import Comment from './Comment'
import {FaComment} from 'react-icons/fa';
import {FaThumbsUp} from 'react-icons/fa';
import {FaRegUser} from 'react-icons/fa';
import {useState} from 'react';
import AddComment from './AddComment'


const Post = ({post, index}) => {

  const[postData,setPostData]=useState({});
  const[activeIndex,setActiveIndex]=useState(-1);


const addComment = (comment) =>{
  const cuser ="User 1";
  const newcomment ={cuser,comment};
  const newComments =[...postData.comments,newcomment]
  post.comments = newComments;
  console.log(postData)
}

const handleClick=(index) =>{
  setActiveIndex(index);
}

    return (
        <div className="c-row">
            <div className="p-row">
              <FaRegUser size={40}/>
              <h6 className="texts">{post.User} {post.text}</h6>
            </div>
           
          {

               post.comments.map((cmnt,idx) => <div className="d-row"><Comment singleComment={cmnt} indx={idx}/></div> ) 
              
          }

<div>
              <div className="flex-container-2">
                 <div className="p-2"><h6 className="texts">Comments:{post.comments.length}Likes:{post.likes}</h6></div>
              </div>
              <div className="flex-container-1">
                <div className="flex-container-1"><FaComment size={40}/> <h6 className="texts">Like</h6></div>
                <div className="flex-container-1" >
                <FaThumbsUp size={40} 
                  onClick={() => handleClick(index)} />
                {
                  (activeIndex === index) && <div><AddComment  onAddComment={addComment}/></div> 
                }
              
                <h6 className="texts">Comment</h6></div>
              </div>
            </div>
         
          
        </div>
    )
}

export default Post
