function App() {

  const[posts,setPosts] = useState([])


const getPost=()=>{
  fetch('./posts.json'
  ,{
    headers : { 
      'Content-Type': 'application/json',
      'Accept': 'application/json'
     }
  }
  )
    .then(function(response){
      console.log(response)
      return response.json();
    })
    .then(function(posts) {
      console.log(posts);
      setPosts(posts)
    });
}
useEffect(()=>{
  getPost()
},[])
  return (
    <div className="App">

    </div>
  );
}

export default App;
