export const addLikeReducer =(state =[],action) =>{
    switch(action.type){
      case 'ADDLIKE' : 
            state = action.up
            const tlikes = state[action.indx].likes +1;
            Object.assign(state[action.indx].likes,tlikes)
            return state;
    default : return state;
    }
}

export default addLikeReducer;
