import React from 'react'
import Comment from './Comment'
import {FaComment} from 'react-icons/fa';
import {FaThumbsUp} from 'react-icons/fa';


const Post = ({post, index}) => {
 
    return (
        
        <div>
          <div className="d-row">
            <div>
            <h6> Post :{index+1} </h6>
            <h6>User :{post.User} {post.text}</h6></div>


            <div className="d-flex bd-highlight example-parent">
    <div className="p-2 flex-fill bd-highlight col-example"><FaComment/></div>
    <div className="p-2 flex-fill bd-highlight col-example"><h6>Like</h6></div>
    <div className="p-2 flex-fill bd-highlight col-example"><FaThumbsUp/></div>
    <div className="p-2 flex-fill bd-highlight col-example"><h6>Comment</h6></div>
  </div>
           
    
        </div>
          {
               post.comments.map((cmnt,idx) => <div className="d-row"><Comment singleComment={cmnt} indx={idx}/></div> ) 
              
          }
         
          
        </div>
    )
}

export default Post
