const deletePostReducer =(state =[],action) =>{
    switch(action.type){ 
        case 'DELETEPOST' : 
        state = action.up
        let newState = [...state];
        newState.splice(action.index, 1);
        return newState;
      
        default : return state;
    }
}

export default deletePostReducer
