import React from 'react'
import Comment from './Comment'
import {FaComment} from 'react-icons/fa';
import {FaThumbsUp} from 'react-icons/fa';
import {FaRegUser} from 'react-icons/fa';
import {useState} from 'react';
import {useSelector,useDispatch} from 'react-redux'
import {increment,decrement} from '../Actions'


const Post = () => {
  const dispatch = useDispatch();
  const posts = useSelector(state => state.postadd);
  return (
    <div className="flex-column">
      {
        posts.map((post)=>{
          <div className="flex-column-1">
            <div className="flex-column-2">
              <FaRegUser size={40}/>
              <h6 className="texts">{post.User} {post.text}</h6>
            </div>
           
          {

               post.comments.map((cmnt,idx) => <div className="flex-column-3"><Comment singleComment={cmnt} indx={idx}/></div> ) 
              
          }

<div>
              <div className="flex-container-2">
                 <div className="p-2"><h6 className="texts">Comments:{post.comments.length}Likes:{post.likes}</h6></div>
              </div>
              <div className="flex-container-1">
                <div className="flex-container-1"><FaComment size={40}/> <h6 className="texts">Like</h6></div>
                <div className="flex-container-1" >
                <FaThumbsUp size={40} />
              
              
                <h6 className="texts">Comment</h6></div>
              </div>
            </div>
         
          
        </div>
        })
      }
    </div>
  )
}

export default Post
