import React from 'react'
import Comment from './Comment'
import {FaComment} from 'react-icons/fa';
import {FaThumbsUp} from 'react-icons/fa';


const Post = ({post, index}) => {
 
    return (
        
          <div className="d-row">
            <div>
            <h6 style="color: #C7AA8D; font-size:3em;"> Post :{index+1} </h6>
            <h6>User :{post.User} {post.text}</h6></div>
            <div>
          <div className="flex-container">
      <div className="p-2"><FaComment/></div>
      <div className="p-2"><h6>Like</h6></div>
      <div className="p-2"><FaThumbsUp/></div>
      <div className="p-2"><h6>Comment</h6></div>
    </div>
           
    
        </div>
          {
               post.comments.map((cmnt,idx) => <div className="d-row"><Comment singleComment={cmnt} indx={idx}/></div> ) 
              
          }
         
          
        </div>
    )
}

export default Post
