import { useState,useEffect } from 'react'

function App() {

  const[posts,setPosts] = useState([])


const getPost=()=>{
  fetch('assignment-app\posts.json\posts')
  // .then(res => res.json()) // comment this out for now
  .then(res => res.text())          // convert to plain text
  .then(text => console.log(text))  
  fetch('./posts.json/posts')
    .then(function(response){
      console.log(response)
      return response.json();
    })
    .then(function(posts) {
      console.log(posts);
      setPosts(posts)
    });
}
useEffect(()=>{
  getPost()
},[])
  return (
    <div className="App">
      {
       posts && posts.length>0 && posts.map((item)=><p>{item}</p>)
     }
    </div>
  );
}

export default App;
