export const addpost =(newPost) =>{
    return{
        type:'ADDPOST',
        newp : newPost
    };
};

export const addcomment =(index,newComment) =>{
    return{
        type:'ADDCOMMENT',
        newc : newComment,
        indx: index
    };
};

export const show =() =>{
    return{
        type:'SHOWCOMMENT'
    }
}
